    ll j = 1e6;
    for(ll i = 0 ; i <= 1e6 ; i++)
    {
        ll temp = i*i*i+i*i*j+i*j*j+j*j*j;
        while(temp >= n && j >= 0)
        {
            res = std::min(res,temp);
            j--;
            temp = i*i*i+i*i*j+i*j*j+j*j*j;
        }
    }
