//    std::cin.tie(nullptr);
//    std::cout.tie(nullptr);
    std::cin>>n>>start_x>>start_y>>end_x>>end_y;
    for(int i = 1 ; i <= n ; i++)std::cin>>(a[i]+1);
    bfs();
    return 0;
}

