```cpp
#include<bits/stdc++.h>

typedef long long ll;

const int N = 2e3+10,M = 2e4+10,INF = 0x3f3f3f3f,mod = 1e9+7;

int a[N][N];

void solve()
{
    int n,cnt = 0,max = 0;
    std::cin>>n;
    for(int i = 1 ; i <= n ; i++)
    {
        std::string s;
        std::cin>>s;
        for(int j = 1 ; j <= n ; j++)
        {
            a[i][j] = s[j-1] - '0';
            cnt += (a[i][j] == 1);
        }
    }
    for(int j = 1 ; j <= n ; j++)
    {
        int y = j,temp = 0;
        for(int i = 1 ; i <= n ; i++)
        {
            if(a[i][y] == 1)temp++;
            y++;
            if(y==n+1)y = 1;
        }
        max = std::max(max,temp);
    }
    std::cout<<cnt - max + (n - max)<<'\n';
}

int main()
{
    std::ios::sync_with_stdio(false);
    std::cin.tie(nullptr);
    std::cout.tie(nullptr);
