                {
                    cnt0++;
                    tag = true;
                }
            }
            if(cnt0==cnt1)res++;
            else
            {
                if(cnt0 > cnt1 && (cnt0 - cnt1) % 3 == 0)
                {
                    if(cnt >= (cnt0 - cnt1)/3 )res++;
                }
            }
        }
    }
    std::cout<<res<<'\n';
}

